import numpy

# the number
num =''

maxProduct = 0;

# look at each 13-long substring of the number
for i in range(len(num) - 13):
    cur = num[i:i+13];
    curNum = [];

    # convert to array of ints
    for j in range(len(cur)):
        curNum.append(int(cur[j]))
        
    # check if new greatest product was found
    curProd = numpy.prod(curNum)
    if curProd > maxProduct:
        maxProduct = curProd
        nums = cur

print(maxProduct)
print(nums)
